    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        // TODO add your handling code here:
        int i=0;
        int t1=0;
        int t2=0;
        int t3=0;
        int t4=0;
        int t5=0;
        int t6=0;
        int t7=0;
        if(!"".equals(jTextArea1.getText())){
            i++;
            t1=Integer.parseInt(jTextArea1.getText());
        }
        if(!"".equals(jTextArea2.getText())){
            i++;
            t2=Integer.parseInt(jTextArea2.getText());
        }
        if(!"".equals(jTextArea3.getText())){
            i++;
            t3=Integer.parseInt(jTextArea3.getText());
        }
        if(!"".equals(jTextArea4.getText())){
            i++;
            t4=Integer.parseInt(jTextArea4.getText());
        }
        if(!"".equals(jTextArea5.getText())){
            i++;
            t5=Integer.parseInt(jTextArea5.getText());
        }
        if(!"".equals(jTextArea6.getText())){
            i++;
            t6=Integer.parseInt(jTextArea6.getText());
        }
        if(!"".equals(jTextArea7.getText())){
            i++;
            t7=Integer.parseInt(jTextArea7.getText());
        }
        int arr[]=new int[7];
        if(t1!=0){
            arr[0]=t1;
        }
        if(t2!=0){
            arr[1]=t2;
        }
        if(t3!=0){
            arr[2]=t3;
        }
        if(t4!=0){
            arr[3]=t4;
        }
        if(t5!=0){
            arr[4]=t5;
        }
        if(t6!=0){
            arr[5]=t6;
        }
        if(t7!=0){
            arr[6]=t7;
        }
        Arrays.sort(arr);
        if(i%2!=0){
            int n=(i+1)/2;
            jLabel7.setText("Median :"+Integer.toString(arr[n-1]));
        }
        else{
            int n=i/2;
            int num=(arr[n-1]+arr[n])/2;
            jLabel7.setText("Median :"+Integer.toString(num));
        }
    }                                        

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        // TODO add your handling code here:
        ArrayList<Integer>list=new ArrayList<>();
        
        if(!"".equals(jTextArea1.getText())){
            list.add(Integer.valueOf(jTextArea1.getText()));
    }
        if(!"".equals(jTextArea2.getText())){
            list.add(Integer.valueOf(jTextArea2.getText()));
    }
        if(!"".equals(jTextArea3.getText())){
            list.add(Integer.valueOf(jTextArea3.getText()));
    }
        if(!"".equals(jTextArea4.getText())){
            list.add(Integer.valueOf(jTextArea4.getText()));
    }
        if(!"".equals(jTextArea5.getText())){
            list.add(Integer.valueOf(jTextArea5.getText()));
    }
        if(!"".equals(jTextArea6.getText())){
            list.add(Integer.valueOf(jTextArea6.getText()));
    }
        if(!"".equals(jTextArea7.getText())){
            list.add(Integer.valueOf(jTextArea7.getText()));
    }
        Map<Integer, Integer> frequencyMap=new HashMap<>();
        for(Integer num: list){
            frequencyMap.put(num, frequencyMap.getOrDefault(num, 0)+1);
            
        }
        int mostFrequentNum=-1;
        int maxFrequency=0;
        for(Map.Entry<Integer, Integer> entry : frequencyMap.entrySet()){
            if(entry.getValue() > maxFrequency){
                mostFrequentNum=entry.getKey();
                maxFrequency=entry.getValue();
            }
            
        }
        jLabel5.setText("Mode : "+mostFrequentNum);
    }                                        
    public void actionPerformed(ActionEvent event){
        String[] datastr=jTextArea1.getText().trim().split("\\s*,\\s*");
        int dataArray[]=Arrays.stream(datastr).mapToInt(Integer:: parseInt).toArray();
        JButton sourceEvent=(JButton)event.getSource();
        if(sourceEvent==jButton3){
            jLabel6.setText("Mean: "+calmean(dataArray));
        }else if(sourceEvent==jButton4){
            jLabel7.setText("Median: "+calMedian(dataArray));
        }else if(sourceEvent==jButton2){
            jLabel5.setText("Mode: "+calMode(dataArray));
        }else if(sourceEvent==jButton5){
            jLabel6.setText("Mean: "+calmean(dataArray));
            jLabel7.setText("Median: "+calMedian(dataArray));
            jLabel5.setText("Mode: "+calMode(dataArray));
        }
    }
