public static double calmean(int[] userdata){
int sum=0;
for(int userdatum: userdata){
sum+=userdatum;

}
return (double)sum/userdata.length;
}
public static double calMedian(int[] userdata){
    Arrays.sort(userdata);
    int middle= userdata.length/2;
    if(userdata.length%2==1){
        return userdata[middle];    }
    else{
       return (userdata[middle-1]+userdata[middle])/2.0;
    }
}
public static double calMode(int[] userdata)
{
    int maxvalue=0;
    int maxcount=0;
    for(int userdatum: userdata){
        int count=0;
        for(int datum: userdata){
            if(datum==userdatum) ++count;
    }
        if(count>maxcount){
            maxcount=count;
            maxvalue=userdatum;
        }
    }
    return (double) maxvalue;
    
}
